const ethers = require('ethers')

module.exports = decodeTxCalldata

function decodeTxCalldata (txData, fun, solcMetadata) {
  var iface = new ethers.utils.Interface(solcMetadata.output.abi);
  var fun = iface.functions.fun;

  var tx = {
    data: txData
  };
  return iface.parseTransaction(tx).args;
}
