var bel = require('bel')
var csjs = require('csjs-inject')

module.exports = displayInputContainer

function displayInputContainer({name, theme: {classes: css}, type, input}) {
  return bel`
    <div class=${css.inputContainer}>
      <div class=${css.inputParam}>${name}</div>
      <div class=${css.inputFields}>${input}</div>
    </div>`
}


